/*
* Custom CSS styles
*
* Global by default, but use `&` to scope to just this content, e.g.
*
*   & .foo {
*     color: 'red'
*   }
*/

.wallets-video-styling video { 
    border-radius: 30px;
    border-width: 2px;
    border-color: #14f195;
}

/* For the one click commerce section, have the copy show up before the video on mobile then have the copy show up on the right on desktop */
.columns-reserve-order .builder-column:nth-of-type(2) {
    order: 1;
}

.columns-reserve-order .builder-column:nth-of-type(1) {
    order: 2;
}

@media all and (min-width: 768px) {
    .columns-reserve-order .builder-column:nth-of-type(2) {
        order: 2;
    }

    .columns-reserve-order .builder-column:nth-of-type(1) {
        order: 1;
    }
}
